import React from "react";
import { useNavigate } from "react-router-dom";
import "./InfoContainer.css";

const InfoContainer = () => {
  const navigate = useNavigate();

  const sections = [
    { title: "Buying", text: "Learn how to buy items safely and easily.", path: "/buying" },
    { title: "Account", text: "Manage your profile, settings, and preferences.", path: "/account" },
    { title: "Selling", text: "Start selling your items to a large audience.", path: "/selling" },
    { title: "Safety & Policy", text: "Read our safety guidelines and company policies.", path: "/safety-policy" },
  ];

  return (
    <div className="info-main">
      <h1 className="info-title">Help & Support</h1>
      <div className="info-column">
        {sections.map((section, index) => (
          <div className="info-box" key={index}>
            <h2>{section.title}</h2>
            <p>{section.text}</p>
            <button onClick={() => navigate(section.path)}>Learn More</button>
          </div>
        ))}
      </div>
    </div>
  );
};

export default InfoContainer;
