import React from "react";
import "./Home.css";

const HomePage = () => {
  return (
    <div className="home-wrapper">
      
      {/* --- First Full-Width Box --- */}
      <section className="full-width-box home-section">
        <div className="section-content">
          <h1>The Live Shopping Marketplace</h1>
          <p>Shop, sell, and connect around the things you love.</p>
          <button className="download-btn">Shop Now</button>
        </div>
        <div className="section-image">
          <img src="assets/Hero.png" alt="Live Shopping" />
        </div>
      </section>

      {/* --- Second Full-Width Box (Join In the Fun) --- */}
      <section className="full-width-box join-section">
        <div className="section-image">
          <img src="assets/joinfun.png" alt="Join In the Fun" />
        </div>
        <div className="section-content">
          <h1>Join In the Fun</h1>
          <p>
            Take part in fast-paced auctions, incredible flash sales, live show
            giveaways, and so much more.
          </p>
          <button className="download-btn">Download WhatNew</button>
        </div>
      </section>

    </div>
  );
};

export default HomePage;
