const express = require("express");
const cors = require("cors");
const mongoose = require("mongoose");
require("dotenv").config();

const app = express();
app.use(cors());
app.use(express.json());

// Connect MongoDB
mongoose.connect(process.env.MONGO_URI, {
  useNewUrlParser: true,
  useUnifiedTopology: true
})
.then(() => console.log("MongoDB connected"))
.catch(err => console.log(err));

// Job Schema
const jobSchema = new mongoose.Schema({
  title: String,
  description: String,
  location: String,
  type: String,
  salary: String
});

const Job = mongoose.model("Job", jobSchema);

// Seed database with initial 10 jobs if empty
const seedJobs = async () => {
  const count = await Job.countDocuments();
  if (count === 0) {
    const initialJobs = [
      { title: "Frontend Developer", description: "React & JS required", location: "Remote", type: "Full-time", salary: "₹6-8 LPA" },
      { title: "Backend Developer", description: "Node.js & MongoDB", location: "Bangalore", type: "Full-time", salary: "₹7-10 LPA" },
      { title: "UI/UX Designer", description: "Figma & Adobe XD", location: "Chennai", type: "Part-time", salary: "₹3-5 LPA" },
      { title: "DevOps Engineer", description: "AWS & CI/CD", location: "Remote", type: "Full-time", salary: "₹8-12 LPA" },
      { title: "Product Manager", description: "Agile & Scrum", location: "Mumbai", type: "Full-time", salary: "₹12-15 LPA" },
      { title: "QA Engineer", description: "Manual & Automation", location: "Remote", type: "Full-time", salary: "₹5-7 LPA" },
      { title: "Marketing Specialist", description: "SEO & Social Media", location: "Delhi", type: "Part-time", salary: "₹4-6 LPA" },
      { title: "Data Analyst", description: "Python & SQL", location: "Remote", type: "Full-time", salary: "₹6-9 LPA" },
      { title: "Mobile Developer", description: "React Native / Flutter", location: "Hyderabad", type: "Full-time", salary: "₹7-11 LPA" },
      { title: "Content Writer", description: "Technical & Creative", location: "Remote", type: "Part-time", salary: "₹3-5 LPA" }
    ];
    await Job.insertMany(initialJobs);
    console.log("Seeded 10 initial jobs");
  }
};

seedJobs();

// Routes
app.get("/jobs", async (req, res) => {
  const jobs = await Job.find();
  res.json(jobs);
});

app.post("/jobs", async (req, res) => {
  const newJob = new Job(req.body);
  await newJob.save();
  res.json(newJob);
});

const PORT = process.env.PORT || 5000;
app.listen(PORT, () => console.log(`Server running on port ${PORT}`));
