import React, { useState, useEffect } from "react";
import "./About.css";

const About = () => {
  // Investors section images
  const investorImages = [
    "/assets/partner.png",
    "/assets/partner-y.png",
    "/assets/partner-x.png",
    "/assets/operator.png",
    "/assets/partner1.png",
    "/assets/partner2.png",
    "/assets/partner3.png",
    "/assets/partner4.png",
  ];

  // Carousel images
  const carouselImages = [
    "/images/img1.jpg",
    "/images/img2.jpg",
    "/images/img3.jpg",
    "/images/img4.jpg",
    "/images/img5.jpg",
  ];

  // State for carousel
  const [currentIndex, setCurrentIndex] = useState(0);

  // Auto-slide every 5 seconds
  useEffect(() => {
    const interval = setInterval(() => {
      setCurrentIndex((prevIndex) =>
        prevIndex === carouselImages.length - 1 ? 0 : prevIndex + 1
      );
    }, 5000);

    return () => clearInterval(interval);
  }, [carouselImages.length]);

  return (
    <div className="about-container">
      {/* ===== About Section ===== */}
      <div className="divbox-container">
        {/* Left Side: Text */}
        <div className="divbox-text">
          <h2>About Us</h2>
          <p>
            We aim to enable anyone to turn their passion into a business & bring
            people together through commerce.
          </p>
        </div>

        {/* Right Side: Image */}
        <div className="divbox-image">
          <img src="/assets/Hero5.png" alt="About Us" />
        </div>
      </div>

     

      {/* ===== Investors Section ===== */}
      <div className="imagegrid-section">
        <h2 className="section-heading">Our Investors</h2>

        <div className="image-grid">
          {investorImages.map((img, index) => (
            <div key={index} className="image-card">
              <img src={img} alt={`Investor ${index + 1}`} />
            </div>
          ))}
        </div>
      </div>

  {/* ===== Carousel Section ===== */}
      <h2 className="section-heading">Our Gallery</h2>
      <div className="carousel-container">
        <div className="carousel-slide">
          <img
            src={carouselImages[currentIndex]}
            alt={`Slide ${currentIndex}`}
          />
        </div>

        <div className="carousel-dots">
          {carouselImages.map((_, index) => (
            <span
              key={index}
              className={`dot ${index === currentIndex ? "active" : ""}`}
              onClick={() => setCurrentIndex(index)}
            ></span>
          ))}
        </div>
      </div>

    </div>
  );
};

export default About;
