import React from "react";
import "./BecomeSeller.css";

const testimonials = [
  {
    content: "This product changed my life! Highly recommend it.",
    image: "assets/person1.jpg",
    name: "Alice Johnson",
    title: "CEO, Company A",
  },
  {
    content: "Excellent service and fantastic support team.",
    image: "assets/person2.jpg",
    name: "Bob Smith",
    title: "Marketing Manager, Company B",
  },
  {
    content: "Affordable, high-quality, and fast delivery. Love it!",
    image: "assets/person3.jpg",
    name: "Catherine Lee",
    title: "Entrepreneur",
  },
  {
    content: "A truly remarkable experience from start to finish.",
    image: "assets/person4.jpg",
    name: "David Kim",
    title: "Designer, Company D",
  },
];



const cardsData = [
  {
    title: "Your audience awaits",
    description:
      "Reach millions of eager buyers on WhatNew—shoppers as excited to discover your products as you are to sell them."
  },
  {
    title: "You belong here",
    description:
      "Whether you’re new or seasoned, young or old, tech-savvy or just starting out—there’s a place for you on WhatNew. Our sellers earn an average of ₹25K a year!"
  },
  {
    title: "Sell your way",
    description:
      "On WhatNew, you can sell in real-time through live shows or list products in your store for buyers to shop at their own pace—or do both. The choice is yours!"
  }
];



const HomePage = () => {
  return (
    <div>
      {/* Promo Section */}
      <div className="promo-container">
        <div className="promo-content">
          <h1>Make more selling what you love.</h1>
          <p>Show and sell. Go live on WhatNew.</p>
        </div>
      </div>

      {/* WhatNew Section */}
      <div className="whatnew-container">
        <div className="whatnew-content">
          <h1>What is WhatNew?</h1>
          <p>
            WhatNew is a shopping experience like no other, where anyone can sell what they love.
          </p>
        </div>
      </div>

      {/* Cards Section */}
      <div className="cards-container">
        {cardsData.map((card, index) => (
          <div className="card" key={index}>
            <h2>{card.title}</h2>
            <p>{card.description}</p>
          </div>
        ))}
      </div>


        {/* Sellers Section */}
      <div className="sellers-section">
        {/* Left Image */}
        <div className="sellers-image">
          <img src="assets/Become1.jpg" alt="Selling on WhatNew" />
        </div>

        {/* Content */}
        <div className="sellers-content">
          <h2>Discover why sellers are choosing WhatNew</h2>

          <h3>Make more selling your way</h3>
          <p>
            Selling live on WhatNew is exciting—and customers make 10x more transactions here than on other peer-to-peer marketplaces. And when you’re not live, your WhatNew store keeps the sales rolling.
          </p>

          <h3>Reach a huge audience of enthusiasts</h3>
          <p>
            Connect with millions of buyers on WhatNew—the largest live shopping app in North America and Europe. Our buyers aren’t just browsing; they’re passionate about discovering unique finds from sellers like you.
          </p>

          <h3>We make it easy</h3>
          <p>
            Our integrated tools and support handle payments, shipping, and everything else, so you can focus on selling what you love.
          </p>

          <h3>Sell with joy</h3>
          <p>
            Live selling on WhatNew isn’t just exciting—it’s pure fun. Share your passion with like-minded shoppers and turn them into loyal fans.
          </p>

          <p className="source">
            * Source: Bloomberg Second Measure; March 31, 2023 — March 31, 2024*
          </p>
        </div>
      </div>





    </div>
  );
};

export default HomePage;
