import React, { useState } from "react";
import { Link } from "react-router-dom";
import "./Header.css";

const Header = () => {
  const [menuOpen, setMenuOpen] = useState(false);

  return (
    <header className="modern-header">
      <div className="header-inner">

        {/* ðŸ”¹ Logo image that links to Home */}
        <Link to="/" className="logo-link">
          <img
            src="/assets/whatnew.jpg"  // ðŸ–¼ï¸ Replace with your logo image path
            alt="WhatNew Logo"
            className="header-logo"
          />
        </Link>

        <button
          className={`menu-toggle ${menuOpen ? "open" : ""}`}
          onClick={() => setMenuOpen(!menuOpen)}
          aria-label="Toggle menu"
        >
          <span></span><span></span><span></span>
        </button>

        {/* ðŸ”¹ Navigation */}
        <nav className={`nav ${menuOpen ? "active" : ""}`}>
          <ul className="nav-list">
            {/* âœ… Become a Seller button/image */}
            <li>
              <Link to="/become-seller" className="seller-btn">
                Become a Seller
              </Link>
              {/* OR if you want an image instead of text:
              <Link to="/become-seller">
                <img src="/assets/become-seller.png" alt="Become a Seller" className="seller-img" />
              </Link> */}
            </li>
            <li><Link to="/blog">Blog</Link></li>
            <li><Link to="/careers">Careers</Link></li>
            <li><Link to="/about">About Us</Link></li>
            <li><Link to="/faq">FAQ</Link></li>
            <li><Link to="/affiliates">Affiliates</Link></li>
          </ul>
        </nav>
      </div>
    </header>
  );
};

export default Header;
