import React, { useState } from "react";
import "./Blog.css";

const TabsGrid = () => {
  const [activeTab, setActiveTab] = useState(1);

  const tabsData = [
    {
      tabName: "Latest",
      content: [
        [
          { img: "/assets/studio.jpg", title: "Product, Latest 7/8/25", desc: "Product Spotlight: Weekly Order Reports, Seller Hub Home, and Improvements to Order & Shipment Search.", link: "#" },
          { img: "/assets/tread.png", title: "Banana", desc: "Yellow bananas.", link: "#" },
          { img: "/assets/what.png", title: "Cherry", desc: "Sweet cherries.", link: "#" },
        ],
        [
          { img: "/images/img4.jpg", title: "Date", desc: "Delicious dates.", link: "#" },
          { img: "/images/img5.jpg", title: "Elderberry", desc: "Healthy elderberries.", link: "#" },
          { img: "/images/img6.jpg", title: "Fig", desc: "Juicy figs.", link: "#" },
        ],
      ],
    },
    // (Other tab data remains the same)
  ];

  return (
    <div className="tabs-container">
      <div className="tabs-nav">
        {tabsData.map((tab, index) => (
          <button
            key={index}
            className={`tab-button ${activeTab === index + 1 ? "active" : ""}`}
            onClick={() => setActiveTab(index + 1)}
          >
            {tab.tabName}
          </button>
        ))}
      </div>

      <div className="tabs-content">
        {tabsData.map((tab, index) => (
          <div
            key={index}
            style={{ display: activeTab === index + 1 ? "block" : "none" }}
          >
            <div className="grid-container">
              {tab.content.flat().map((item, itemIndex) => (
                <div key={itemIndex} className="grid-item">
                  <a href={item.link} className="item-link">
                    <div className="item-image-wrapper">
                      <img src={item.img} alt={item.title} className="item-image" />
                    </div>
                    <div className="item-content">
                      <h3 className="item-title">{item.title}</h3>
                      <p className="item-description">{item.desc}</p>
                    </div>
                  </a>
                </div>
              ))}
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default TabsGrid;
