import React, { useState, useEffect } from "react";
import axios from "axios";
import "./career.css";

export default function Career() {
  const [jobs, setJobs] = useState([]);
  const [selectedJob, setSelectedJob] = useState(null);
  const [resume, setResume] = useState(null);
  const [newJob, setNewJob] = useState({ title: "", description: "", location: "", type: "", salary: "" });

  const [currentPage, setCurrentPage] = useState(1);
  const jobsPerPage = 3;

  useEffect(() => {
    axios.get("http://localhost:5000/jobs")
      .then(res => setJobs(res.data))
      .catch(err => console.log(err));
  }, []);

  const handleNewJobChange = e => setNewJob({ ...newJob, [e.target.name]: e.target.value });

  const handlePostJob = () => {
    if (!newJob.title || !newJob.description) return alert("Please fill all fields!");

    axios.post("http://localhost:5000/jobs", newJob)
      .then(res => {
        setJobs([res.data, ...jobs]);
        setNewJob({ title: "", description: "", location: "", type: "", salary: "" });
        alert("Job posted successfully!");
      })
      .catch(err => console.log(err));
  };

  const indexOfLastJob = currentPage * jobsPerPage;
  const indexOfFirstJob = indexOfLastJob - jobsPerPage;
  const currentJobs = jobs.slice(indexOfFirstJob, indexOfLastJob);

  const paginate = pageNumber => setCurrentPage(pageNumber);

  return (
    <div className="career-container">
      <h2>Career Opportunities</h2>

      <div className="job-list">
        {currentJobs.map(job => (
          <div className="job-card" key={job._id}>
            <h3>{job.title}</h3>
            <p>{job.description}</p>
            <p><strong>Location:</strong> {job.location}</p>
            <p><strong>Type:</strong> {job.type}</p>
            <p><strong>Salary:</strong> {job.salary}</p>
          </div>
        ))}
      </div>

      <div className="pagination">
        {Array.from({ length: Math.ceil(jobs.length / jobsPerPage) }, (_, i) => (
          <button key={i+1} onClick={() => paginate(i+1)}>{i+1}</button>
        ))}
      </div>

      <div className="post-job-form">
        <h3>Post a New Job</h3>
        <input type="text" name="title" placeholder="Job Title" value={newJob.title} onChange={handleNewJobChange} />
        <textarea name="description" placeholder="Job Description" value={newJob.description} onChange={handleNewJobChange}></textarea>
        <input type="text" name="location" placeholder="Location" value={newJob.location} onChange={handleNewJobChange} />
        <input type="text" name="type" placeholder="Job Type" value={newJob.type} onChange={handleNewJobChange} />
        <input type="text" name="salary" placeholder="Salary" value={newJob.salary} onChange={handleNewJobChange} />
        <button onClick={handlePostJob}>Post Job</button>
      </div>
    </div>
  );
}
