import React, { useEffect, useState } from 'react';
import axios from 'axios';
import "./career.css";

export default function JobList() {
  const [jobs, setJobs] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    axios.get('http://localhost:5000/jobs')
      .then(res => setJobs(res.data))
      .catch(err => {
        console.error('Error fetching jobs:', err);
        setJobs([]);
      })
      .finally(() => setLoading(false));
  }, []);

  if (loading) return <div>Loading jobs...</div>;
  if (jobs.length === 0) return <div>No jobs available.</div>;

  return (
    <div className="job-list-container">
      <h2>Open Job Positions</h2>
      <ul style={{ listStyle: 'none', padding: 0 }}>
        {jobs.map(job => (
          <li key={job._id} className="job-item">
            <h3>{job.title}</h3>
            <p><span className="job-label">Company:</span> {job.company}</p>
            <p><span className="job-label">Location:</span> {job.location}</p>
            <p><span className="job-label">Type:</span> {job.type}</p>
            <p><span className="job-label">Salary:</span> {job.salary}</p>
            <p>{job.description}</p>
            <small>Posted on: {new Date(job.postedAt).toLocaleDateString()}</small>
          </li>
        ))}
      </ul>
    </div>
  );
}
