import React, { useState, useEffect, useRef } from "react";
import "./BecomeSeller.css";



// ===== Testimonial Data =====
const testimonials = [
  {
    content:
      "We acquire more customers on WhatNew in a week than we do in our stores in a year.",
    image: "assets/Hobby.png",
    name: "Sammie, Sports Cards Seller",
    title: ".@hobbyboxny",
  },
  {
    content:
      "The community is full of nice, supportive people... I have people who watch me stream 3x a week... I really appreciate the bond I've built with my customers.",
    image: "assets/Theli.png",
    name: "Myra, Beauty Seller",
    title: ".@theliplady",
  },
  {
    content:
      "For me, being able to have a real business on WhatNew that is interactive, has rapid growth, and is just overall fun has been a dream.",
    image: "assets/Brick.png",
    name: "Jon, Toys Seller",
    title: ".@brickluv",
  },
  {
    content:
      "Last August, prior to live selling, I made ₹600 in sales on static platforms. This August, I will hit ₹125k in sales.",
    image: "assets/Caitie.png",
    name: "Caitie, Women's Fashion Seller",
    title: ".@CaitieCo",
  },
];

// ===== Cards Data =====
const cardsData = [
  {
    title: "Your audience awaits",
    description:
      "Reach millions of eager buyers on WhatNew—shoppers as excited to discover your products as you are to sell them.",
  },
  {
    title: "You belong here",
    description:
      "Whether you’re new or seasoned, young or old, tech-savvy or just starting out—there’s a place for you on WhatNew. Our sellers earn an average of ₹25K a year!",
  },
  {
    title: "Sell your way",
    description:
      "On WhatNew, you can sell in real-time through live shows or list products in your store for buyers to shop at their own pace—or do both. The choice is yours!",
  },
];

// ===== FAQ Data =====
const faqs = [
  {
    question: "How do I get paid?",
    answer:
      "You’ll get paid 48–72 hours after your package is delivered. Payments first appear as WhatNew credit in your account, which you can easily cash out to your bank.",
  },
  {
    question: "How does shipping work?",
    answer:
      "We partner with services like USPS and Canada Post to ensure your order is delivered safely and securely. After you complete your sale, we’ll create a prepaid label so you can ship your item hassle-free.",
  },
  {
    question: "What are the fees?",
    answer:
      "8% commission on the item’s sold price. 2.9% + ₹0.30 payment processing fee on the entire transaction. Payment processing applies to the subtotal, tax, and shipping paid by the buyer.",
  },
  {
    question: "When can I schedule a show?",
    answer:
      "You can schedule your show anytime! Viewership is consistent throughout the week, and you can also list items for always-on selling in your WhatNew store.",
  },
  {
    question: "What can I sell?",
    answer:
      "The true question is what can’t you sell on WhatNew! We currently have 50+ categories – electronics, fashion, collectibles, beauty, live plants, and more.",
  },
  {
    question: "Is it safe?",
    answer:
      "Yes! WhatNew has best-in-class support and tools to ensure a smooth shopping experience. Our priority has always been to build a community with trust, reliability, and safety.",
  },
];


// Existing data arrays (testimonials, faqs, cardsData)...

const HomePage = () => {
  const [currentIndex, setCurrentIndex] = useState(0);
  const [activeFAQ, setActiveFAQ] = useState(null);
  const sectionsRef = useRef([]);

  // Auto-slide testimonials
  useEffect(() => {
    const interval = setInterval(() => {
      setCurrentIndex((prevIndex) =>
        prevIndex === testimonials.length - 1 ? 0 : prevIndex + 1
      );
    }, 5000);
    return () => clearInterval(interval);
  }, []);

  // Intersection Observer for fade-up/fade-in
  useEffect(() => {
    const observer = new IntersectionObserver(
      (entries) => {
        entries.forEach((entry) => {
          if (entry.isIntersecting) {
            entry.target.classList.add("show");
          }
        });
      },
      { threshold: 0.1 }
    );

    sectionsRef.current.forEach((section) => {
      if (section) observer.observe(section);
    });

    return () => {
      sectionsRef.current.forEach((section) => {
        if (section) observer.unobserve(section);
      });
    };
  }, []);

  const toggleFAQ = (index) => {
    setActiveFAQ(activeFAQ === index ? null : index);
  };

  return (
    <div>
      {/* ===== Promo Section ===== */}
      <div className="promo-container fade-up" ref={(el) => (sectionsRef.current[0] = el)}>
        <div className="promo-content">
          <h1>Make more selling what you love.</h1>
          <p>Show and sell. Go live on WhatNew.</p>
        </div>
      </div>

      {/* ===== WhatNew Section ===== */}
      <div className="whatnew-container fade-up" ref={(el) => (sectionsRef.current[1] = el)}>
        <div className="whatnew-content">
          <h1>What is WhatNew?</h1>
          <p>WhatNew is a shopping experience like no other, where anyone can sell what they love.</p>
        </div>
      </div>

      {/* ===== Cards Section ===== */}
      <div className="cards-container fade-up" ref={(el) => (sectionsRef.current[2] = el)}>
        {cardsData.map((card, index) => (
          <div className="card fade-up" key={index} ref={(el) => (sectionsRef.current[3 + index] = el)}>
            <h2>{card.title}</h2>
            <p>{card.description}</p>
          </div>
        ))}
      </div>

      {/* ===== Sellers Section ===== */}
      <div className="sellers-section fade-up" ref={(el) => (sectionsRef.current[6] = el)}>
        <div className="sellers-image">
          <img src="assets/Become1.jpg" alt="Selling on WhatNew" />
        </div>
        <div className="sellers-content">
          {/* Content omitted for brevity */}
        </div>
      </div>

      {/* ===== Testimonial Carousel ===== */}
      <div className="testimonial-carousel-container fade-up" ref={(el) => (sectionsRef.current[7] = el)}>
        {/* Carousel content */}
      </div>

      {/* ===== How It Works ===== */}
      <div className="how-it-works-container fade-up" ref={(el) => (sectionsRef.current[8] = el)}>
        <h2>How It Works</h2>
      </div>

      <div className="feature-section fade-up" ref={(el) => (sectionsRef.current[9] = el)}>
        {/* Feature content */}
      </div>

      <div className="reverse-section fade-up" ref={(el) => (sectionsRef.current[10] = el)}>
        {/* Reverse content */}
      </div>

      <div className="image-right-section fade-up" ref={(el) => (sectionsRef.current[11] = el)}>
        {/* Image right content */}
      </div>

      {/* ===== FAQ Section ===== */}
      <div className="faq-container fade-up" ref={(el) => (sectionsRef.current[12] = el)}>
        <h2>Frequently Asked Questions</h2>
        <div className="faq-list">
          {faqs.map((faq, index) => (
            <div
              className={`faq-item ${activeFAQ === index ? "active" : ""} fade-up`}
              key={index}
              onClick={() => toggleFAQ(index)}
              ref={(el) => (sectionsRef.current[13 + index] = el)}
            >
              <div className="faq-question">
                <h3>{faq.question}</h3>
                <span>{activeFAQ === index ? "−" : "+"}</span>
              </div>
              {activeFAQ === index && (
                <div className="faq-answer">
                  <p>{faq.answer}</p>
                </div>
              )}
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default HomePage;
