import React, { useState, useEffect } from "react";
import axios from "axios";
import "./Discovery.css";
import Header2 from "../components/Header2";

export default function Discovery() {
  const [jobs, setJobs] = useState([]);
  const [selectedJob, setSelectedJob] = useState(null);
  const [resume, setResume] = useState(null);
  const [newJob, setNewJob] = useState({
    title: "",
    description: "",
    location: "",
    type: "",
    salary: "",
  });

  // Pagination
  const [currentPage, setCurrentPage] = useState(1);
  const jobsPerPage = 3;

  // API Base URL (Backend URL)
  const API = "http://localhost:5000/api/jobs";

  // Fetch jobs on page load or page change
  useEffect(() => {
    fetchJobs();
  }, [currentPage]);

  const fetchJobs = async () => {
    try {
      const res = await axios.get(`${API}?page=${currentPage}&limit=${jobsPerPage}`);
      setJobs(res.data.jobs || []);
    } catch (err) {
      alert("Failed to load jobs from server!");
    }
  };

  // Apply for job
  const handleApply = async (job) => {
    if (!resume) return alert("Please upload a resume!");

    const formData = new FormData();
    formData.append("resume", resume);

    try {
      await axios.post(`${API}/${job._id}/apply`, formData, {
        headers: { "Content-Type": "multipart/form-data" },
      });

      alert(`Applied to ${job.title} successfully!`);
      setSelectedJob(null);
      setResume(null);
    } catch (err) {
      alert("Failed to apply!");
    }
  };

  // Post new job
  const handleNewJobChange = (e) => {
    setNewJob({ ...newJob, [e.target.name]: e.target.value });
  };

  const handlePostJob = async () => {
    if (!newJob.title || !newJob.description)
      return alert("Please fill all fields!");

    try {
      await axios.post(API, newJob);
      alert("Job posted successfully!");
      setNewJob({
        title: "",
        description: "",
        location: "",
        type: "",
        salary: "",
      });
      fetchJobs();
    } catch (err) {
      alert("Failed to post job!");
    }
  };

  // Pagination display (just 1→10 static for UI)
  const totalPages = 10;

  return (
    <div className="career-container">
      <Header2 />
      <h2 className="career-title">Career Opportunities</h2>

      {/* Jobs List */}
      <div className="job-list">
        {jobs.map((job) => (
          <div className="job-card" key={job._id}>
            <h3>{job.title}</h3>
            <p>{job.description}</p>
            <p><strong>Location:</strong> {job.location}</p>
            <p><strong>Type:</strong> {job.type}</p>
            <p><strong>Salary/Package:</strong> {job.salary}</p>
            <button onClick={() => setSelectedJob(job)}>
              Apply Now
            </button>
          </div>
        ))}
      </div>

      {/* Pagination UI */}
      <div className="pagination">
        {Array.from({ length: totalPages }, (_, i) => (
          <button
            key={i}
            onClick={() => setCurrentPage(i + 1)}
            className={currentPage === i + 1 ? "active-page" : ""}
          >
            {i + 1}
          </button>
        ))}
      </div>

      {/* Apply Form */}
      {selectedJob && (
        <div className="apply-form">
          <h3>Apply for: {selectedJob.title}</h3>
          <p><strong>Salary/Package:</strong> {selectedJob.salary}</p>

          <input
            type="file"
            accept=".pdf,.doc,.docx"
            onChange={(e) => setResume(e.target.files[0])}
          />

          <button onClick={() => handleApply(selectedJob)}>Submit Application</button>
          <button className="cancel-btn" onClick={() => setSelectedJob(null)}>
            Cancel
          </button>
        </div>
      )}

      {/* Post Job Form */}
      <div className="post-job-form">
        <h3>Post a New Job</h3>

        <input
          type="text"
          name="title"
          placeholder="Job Title"
          value={newJob.title}
          onChange={handleNewJobChange}
        />
        <textarea
          name="description"
          placeholder="Job Description"
          value={newJob.description}
          onChange={handleNewJobChange}
        ></textarea>
        <input
          type="text"
          name="location"
          placeholder="Location"
          value={newJob.location}
          onChange={handleNewJobChange}
        />
        <input
          type="text"
          name="type"
          placeholder="Job Type"
          value={newJob.type}
          onChange={handleNewJobChange}
        />
        <input
          type="text"
          name="salary"
          placeholder="Salary / Package"
          value={newJob.salary}
          onChange={handleNewJobChange}
        />

        <button onClick={handlePostJob}>Post Job</button>
      </div>
    </div>
  );
}
