      <section className="interview-process">
        <div className="container">
          <h2 className="iv-heading fade-in" style={{ ["--delay"]: "0ms" }}>
            Our Interview Process
          </h2>
          <p className="iv-intro fade-in" style={{ ["--delay"]: "80ms" }}>
            This process may vary depending on the role, but generally here’s what to expect if you apply.
          </p>

          <ol className="timeline">
            {steps.map((s, i) => {
              const delay = `${i * 120}ms`;
              const titleDelay = `${i * 120 + 40}ms`;
              const descDelay = `${i * 120 + 160}ms`;

              return (
                <li
                  key={i}
                  className={`iv-step ${i % 2 === 0 ? "left" : "right"}`}
                  style={{ ["--delay"]: delay }}
                >
                  <div className="iv-marker-wrapper">
                    <div
                      className="iv-marker fade-up"
                      style={{ ["--delay"]: delay }}
                    >
                      <span>{i + 1}</span>
                    </div>
                  </div>

                  <div className="iv-card">
                    <button
                      className="iv-card-inner"
                      aria-expanded={expanded === i}
                      onClick={() => toggle(i)}
                    >
                      <h3 className="iv-step-title fade-in" style={{ ["--delay"]: titleDelay }}>
                        {s.title}
                      </h3>
                      <p className="iv-step-desc fade-in" style={{ ["--delay"]: descDelay }}>
                        {s.description}
                      </p>

                      <span className="iv-chevron">▼</span>
                    </button>

                    <div
                      className={`iv-expand ${expanded === i ? "open" : ""}`}
                      style={{ ["--delay"]: descDelay }}
                    >
                      <p>{s.description}</p>
                    </div>
                  </div>
                </li>
              );
            })}
          </ol>
        </div>
      </section>

      {/* FAQ SECTION */}
      <div className="faq1-container">
        <h2>Frequently Asked Questions</h2>

        <div className="faq1-grid">
          {faqs.map((faq, index) => (
            <div
              key={index}
              className={`faq1-item ${activeFAQ === index ? "active" : ""}`}
              onClick={() => toggleFAQ(index)}
            >
              <div className="faq1-question">
                <h3>{faq.question}</h3>
                <span>{activeFAQ === index ? "−" : "+"}</span>
              </div>

              {activeFAQ === index && (
                <div className="faq-answer">
                  <p>{faq.answer}</p>
                </div>
              )}
            </div>
          ))}
        </div>
      </div>

      {/* QUOTE BLOCK */}
      <div className="quote-block">
        <div className="quote-media">
          <img src="assets/cook.jpeg" alt="Courtney Cook" />
        </div>
        <div className="quote-content">
          <blockquote>
            “The principle that contributes most to our culture is team over ego.
            It’s how we’re able to scale while staying true to our mission.”
          </blockquote>
          <p className="quote-author">
            — <strong>Courtney Cook</strong>, VP, Commerce
          </p>
        </div>
      </div>
    </div>
  );
}
