import React, { useState, useEffect } from "react";
import "./Home.css";

const Home = () => {
  // Images for the third box
  const images = [
    "assets/Hero3.png",
    "assets/Hero2.png",
    "assets/Hero3.png",
    "assets/Hero4.png",
  ];

  const [currentImages, setCurrentImages] = useState(images);

  // Rotate images every 3 seconds
  useEffect(() => {
    const interval = setInterval(() => {
      setCurrentImages((prevImages) => {
        const newImages = [...prevImages];
        const first = newImages.shift(); // remove first image
        newImages.push(first); // add it to the end
        return newImages;
      });
    }, 3000);

    return () => clearInterval(interval);
  }, []);

  return (
    <div>
      {/* First Box */}
      <div className="box-container">
        <div className="box-left">
          <h1>The Live Shopping Marketplace</h1>
          <p>Shop, sell, and connect around the things you love</p>
          <button>Shop Now</button>
        </div>
        <div className="box-right">
          <img src="assets/Hero.png" alt="Live Shopping" />
        </div>
      </div>

      {/* Second Box */}
      <div className="image-left-section">
        <div className="image-column">
          <img src="assets/Hero2.jpeg" alt="Live Shopping" />
        </div>
        <div className="content-column">
          <h1>Join In the Fun</h1>
          <p>
            Take part in fast-paced auctions, incredible flash sales, live
            show giveaways, and so much more
          </p>
          <button>Shop Now</button>
        </div>
      </div>

      {/* Third Box with auto-changing images */}
      <div className="third-box-container">
        <div className="third-box-right">
          <h1>Discover Amazing Deals</h1>
          <p>Explore exclusive offers and shop your favorite products with ease.</p>
          <button>Shop Now</button>
        </div>

        <div className="third-box-left">
          {currentImages.map((img, index) => (
            <img
              key={index}
              src={img}
              alt={`Exciting Shopping ${index + 1}`}
              className="auto-change-img"
            />
          ))}
        </div>
      </div>
    </div>
  );
};

export default Home;
