import React, { useState, useEffect, useRef } from "react";
import "./BecomeSeller.css";

// ===== DATA ARRAYS =====
const testimonials = [
  {
    content:
      "We acquire more customers on WhatNew in a week than we do in our stores in a year.",
    image: "assets/Hobby.png",
    name: "Sammie, Sports Cards Seller",
    title: ".@hobbyboxny",
  },
  {
    content:
      "The community is full of nice, supportive people... I have people who watch me stream 3x a week.",
    image: "assets/Theli.png",
    name: "Myra, Beauty Seller",
    title: ".@theliplady",
  },
  {
    content:
      "For me, being able to have a real business on WhatNew that is interactive, has rapid growth, and is just overall fun.",
    image: "assets/Brick.png",
    name: "Jon, Toys Seller",
    title: ".@brickluv",
  },
  {
    content:
      "Last August, prior to live selling, I made ₹600 in sales on static platforms. This August, I will hit ₹125k in sales.",
    image: "assets/Caitie.png",
    name: "Caitie, Women's Fashion Seller",
    title: ".@CaitieCo",
  },
];

const cardsData = [
  {
    title: "Your audience awaits",
    description:
      "Reach millions of eager buyers on WhatNew—shoppers as excited to discover your products as you are to sell them.",
  },
  {
    title: "You belong here",
    description:
      "Whether you’re new or seasoned, young or old, tech-savvy or just starting out—there’s a place for you on WhatNew. Our sellers earn an average of ₹25K a year!",
  },
  {
    title: "Sell your way",
    description:
      "On WhatNew, you can sell in real-time through live shows or list products in your store for buyers to shop at their own pace—or do both. The choice is yours!",
  },
];

const faqs = [
  {
    question: "How do I get paid?",
    answer:
      "You’ll get paid 48–72 hours after your package is delivered. Payments first appear as WhatNew credit in your account, which you can easily cash out to your bank.",
  },
  {
    question: "How does shipping work?",
    answer:
      "We partner with services like USPS and Canada Post to ensure your order is delivered safely and securely. After you complete your sale, we’ll create a prepaid label so you can ship your item hassle-free.",
  },
  {
    question: "What are the fees?",
    answer:
      "8% commission on the item’s sold price. 2.9% + ₹0.30 payment processing fee on the entire transaction.",
  },
  {
    question: "When can I schedule a show?",
    answer:
      "You can schedule your show anytime! Viewership is consistent throughout the week, and you can also list items for always-on selling in your WhatNew store.",
  },
  {
    question: "What can I sell?",
    answer:
      "We currently have 50+ categories – electronics, fashion, collectibles, beauty, live plants, and more.",
  },
  {
    question: "Is it safe?",
    answer:
      "Yes! WhatNew has best-in-class support and tools to ensure a smooth shopping experience.",
  },
];

const HomePage = () => {
  const [currentIndex, setCurrentIndex] = useState(0);
  const [activeFAQ, setActiveFAQ] = useState(null);
  const fadeRefs = useRef([]);

  // ===== Auto-slide testimonials =====
  useEffect(() => {
    const interval = setInterval(() => {
      setCurrentIndex((prev) =>
        prev === testimonials.length - 1 ? 0 : prev + 1
      );
    }, 5000);
    return () => clearInterval(interval);
  }, []);

  // ===== Fade-up on scroll =====
  const addToFadeRefs = (el) => {
    if (el && !fadeRefs.current.includes(el)) {
      fadeRefs.current.push(el);
    }
  };

  useEffect(() => {
    const observer = new IntersectionObserver(
      (entries) => {
        entries.forEach((entry) => {
          if (entry.isIntersecting) {
            entry.target.classList.add("show");
          }
        });
      },
      { threshold: 0.2 }
    );

    fadeRefs.current.forEach((el) => observer.observe(el));

    return () => observer.disconnect();
  }, []);

  const toggleFAQ = (index) => {
    setActiveFAQ(activeFAQ === index ? null : index);
  };

  return (
    <div>
      {/* ===== Promo Section ===== */}
      <div className="promo-container fade-up" ref={addToFadeRefs}>
        <div className="promo-content">
          <h1>Make more selling what you love.</h1>
          <p>Show and sell. Go live on WhatNew.</p>
        </div>
      </div>

      {/* ===== WhatNew Section ===== */}
      <div className="whatnew-container fade-up" ref={addToFadeRefs}>
        <div className="whatnew-content">
          <h1>What is WhatNew?</h1>
          <p>
            WhatNew is a shopping experience like no other, where anyone can
            sell what they love.
          </p>
        </div>
      </div>

      {/* ===== Cards Section ===== */}
      <div className="cards-container">
        {cardsData.map((card, index) => (
          <div className="card fade-up" key={index} ref={addToFadeRefs}>
            <h2>{card.title}</h2>
            <p>{card.description}</p>
          </div>
        ))}
      </div>

      {/* ===== Sellers Section ===== */}
      <div className="sellers-section fade-up" ref={addToFadeRefs}>
        <div className="sellers-image fade-up">
          <img src="assets/Become1.jpg" alt="Selling on WhatNew" />
        </div>
        <div className="sellers-content fade-up">
          <h2>Discover why sellers are choosing WhatNew</h2>
          <h3>Make more selling your way</h3>
          <p>Selling live on WhatNew is exciting...</p>
          <h3>Reach a huge audience of enthusiasts</h3>
          <p>Connect with millions of buyers on WhatNew...</p>
          <h3>We make it easy</h3>
          <p>Our integrated tools and support handle payments...</p>
          <h3>Sell with joy</h3>
          <p>Live selling on WhatNew isn’t just exciting—it’s pure fun...</p>
        </div>
      </div>

      {/* ===== Testimonial Carousel ===== */}
      <div className="testimonial-carousel-container fade-up" ref={addToFadeRefs}>
        <h1>Hear from real sellers</h1>
        <div className="carousel-wrapper">
          <div className="testimonial-card fade-up">
            <p className="testimonial-content">
              "{testimonials[currentIndex].content}"
            </p>
            <div className="testimonial-info">
              <img
                src={testimonials[currentIndex].image}
                alt={testimonials[currentIndex].name}
                className="testimonial-img fade-up"
              />
              <div>
                <h3 className="testimonial-name">{testimonials[currentIndex].name}</h3>
                <p className="testimonial-title">{testimonials[currentIndex].title}</p>
              </div>
            </div>
          </div>
        </div>
      </div>

      {/* ===== How It Works ===== */}
      <div className="how-it-works-container fade-up" ref={addToFadeRefs}>
        <h2>How It Works</h2>
      </div>

      {/* ===== Feature Section ===== */}
      <div className="feature-section fade-up" ref={addToFadeRefs}>
        <div className="feature-left fade-up">
          <img src="assets/How.png" alt="Feature" />
        </div>
        <div className="feature-right fade-up">
          <h2>Sign up</h2>
          <p>Whether you’re a seasoned pro or just starting out...</p>
        </div>
      </div>

      {/* ===== Reverse Section ===== */}
      <div className="reverse-section fade-up" ref={addToFadeRefs}>
        <div className="reverse-left fade-up">
          <h2>Powerful Features</h2>
          <p>When it comes to selling on WhatNew...</p>
          <h2>Sell in a live show</h2>
          <ul>
            <li>Host a show and chat with your audience in real-time.</li>
            <li>Auction off items, sell items at fixed prices, or run giveaways.</li>
            <li>Camera-shy? No problem – feature your products front-and-center!</li>
            <li>Show off your products and geek out with excited buyers.</li>
          </ul>
          <h2>Sell from your WhatNew store 24/7</h2>
          <ul>
            <li>List products and let buyers shop anytime.</li>
            <li>Keep making sales even when you’re not hosting a show.</li>
          </ul>
        </div>
        <div className="reverse-right fade-up">
          <img src="assets/sell.png" alt="Feature" />
        </div>
      </div>

      {/* ===== Image Right Section ===== */}
      <div className="image-right-section fade-up" ref={addToFadeRefs}>
        <div className="text-left fade-up">
          <h2>Ship and get paid</h2>
          <p>Grab your pre-paid WhatNew shipping labels...</p>
        </div>
        <div className="image-right fade-up">
          <img src="assets/ship.jpg" alt="Seamless Experience" />
        </div>
      </div>

      {/* ===== FAQ Section ===== */}
      <div className="faq-container fade-up" ref={addToFadeRefs}>
        <h2>Frequently Asked Questions</h2>
        <div className="faq-list">
          {faqs.map((faq, index) => (
            <div
              className={`faq-item ${activeFAQ === index ? "active" : ""} fade-up`}
              key={index}
              onClick={() => toggleFAQ(index)}
            >
              <div className="faq-question">
                <h3>{faq.question}</h3>
                <span>{activeFAQ === index ? "−" : "+"}</span>
              </div>
              {activeFAQ === index && (
                <div className="faq-answer">
                  <p>{faq.answer}</p>
                </div>
              )}
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default HomePage;
