import React, { useEffect, useState } from 'react';
import axios from 'axios';
import "./career.css";

export default function JobList() {
  const [jobs, setJobs] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    axios.get('http://localhost:5000/jobs')
      .then(res => setJobs(res.data))
      .catch(err => {
        console.error('Error fetching jobs:', err);
        setJobs([]);
      })
      .finally(() => setLoading(false));
  }, []);

  if (loading) return <div>Loading jobs...</div>;
  if (jobs.length === 0) return <div>No jobs available.</div>;

  return (
    <div style={{ padding: '20px', fontFamily: 'Arial' }}>
      <h2>Open Job Positions</h2>
      <ul style={{ listStyle: 'none', padding: 0 }}>
        {jobs.map(job => (
          <li key={job._id} style={{ marginBottom: '20px', border: '1px solid #ccc', padding: '10px', borderRadius: '8px' }}>
            <h3>{job.title}</h3>
            <p><strong>Company:</strong> {job.company}</p>
            <p><strong>Location:</strong> {job.location}</p>
            <p><strong>Type:</strong> {job.type}</p>
            <p><strong>Salary:</strong> {job.salary}</p>
            <p>{job.description}</p>
            <small>Posted on: {new Date(job.postedAt).toLocaleDateString()}</small>
          </li>
        ))}
      </ul>
    </div>
  );
}
