import React, { useRef, useEffect } from "react";
import emailjs from "@emailjs/browser";
import "./Home.css";

const Home = () => {
  const form = useRef();

  const sendEmail = (e) => {
    e.preventDefault();
    emailjs
      .sendForm(
        "service_qompota",
        "template_95iekz2",
        form.current,
        "tkG-UJe02yYKmGQ-m"
      )
      .then(
        (result) => {
          console.log(result.text);
          alert("✅ Subscription successful! Thank you.");
          form.current.reset();
        },
        (error) => {
          console.log(error.text);
          alert("❌ Something went wrong. Please try again.");
        }
      );
  };

  useEffect(() => {
    // If user prefers reduced motion, don't animate
    const reduceMotion = window.matchMedia("(prefers-reduced-motion: reduce)").matches;
    if (reduceMotion) {
      document.querySelectorAll(".fade-in, .fade-up").forEach(el => el.classList.add("show"));
      return;
    }

    // Good defaults: threshold small so it triggers as element comes into view
    const options = {
      root: null,
      rootMargin: "0px 0px -10% 0px", // trigger a little before fully in view
      threshold: 0.12,
    };

    const observer = new IntersectionObserver((entries, obs) => {
      entries.forEach(entry => {
        const el = entry.target;
        if (entry.isIntersecting) {
          el.classList.add("show");
          // if you want it to trigger only once, unobserve after showing:
          obs.unobserve(el);
        }
      });
    }, options);

    // Wait for next frame so layout is settled (helps if images push things)
    requestAnimationFrame(() => {
      const els = document.querySelectorAll(".fade-in, .fade-up");
      if (!els || els.length === 0) return;

      els.forEach(el => {
        // If element is already visible (e.g. on short pages), show immediately
        const rect = el.getBoundingClientRect();
        if (rect.top < window.innerHeight && rect.bottom > 0) {
          el.classList.add("show");
          return;
        }
        observer.observe(el);
      });
    });

    return () => observer.disconnect();
  }, []);

  return (
    <div>
      {/* ===== First Box ===== */}
      <div className="box-container fade-up">
        <div className="box-left">
          <h1>The Live Shopping Marketplace</h1>
          <p>Shop, sell, and connect around the things you love.</p>
          <button>Shop Now</button>
        </div>
        <div className="box-right">
          <img src="assets/Hero.png" alt="Live Shopping" />
        </div>
      </div>

      {/* ===== Second Box ===== */}
      <div className="image-left-section fade-in">
        <div className="image-column">
          <img src="assets/Hero2.jpeg" alt="Live Shopping" />
        </div>
        <div className="content-column">
          <h1>Join In the Fun</h1>
          <p>
            Take part in fast-paced auctions, incredible flash sales, live show
            giveaways, and so much more.
          </p>
          <button>Shop Now</button>
        </div>
      </div>

      {/* ===== Third Box ===== */}
      <div className="third-box-container fade-up">
        <div className="third-box-right">
          <h1>We've Got It All</h1>
          <p>
            Explore 250+ categories, including fashion, coins, sports & Pokémon
            cards, sneakers, and more.
          </p>
          <button>Shop Now</button>
        </div>
        <div className="third-box-left">
          <img src="assets/c425.jpeg" alt="Exciting Shopping" />
        </div>
      </div>

      {/* ===== Fourth Box ===== */}
      <div className="fourth-box-container fade-in">
        <div className="fourth-box-left">
          <h1>Find Incredible Deals on Name Brands</h1>
          <p>
            From the brands you love, to hard-to-find specialty products. There's
            a deal on whatever you're looking for.
          </p>
          <button>Shop Now</button>
        </div>
        <div className="fourth-box-right">
          <img src="assets/images.png.png" alt="Amazing Deals" />
        </div>
      </div>

      {/* ===== Subscribe Section ===== */}
      <div className="subscribe-container fade-up">
        <h2>Subscribe to Our Newsletter</h2>
        <p>Stay updated with the latest deals, offers, and live events!</p>

        <form ref={form} onSubmit={sendEmail} className="subscribe-box">
          <input type="email" name="user_email" placeholder="Enter your email" required />
          <button type="submit">Subscribe</button>
        </form>
      </div>
    </div>
  );
};

export default Home;
