import React, { useState, useEffect, useRef } from "react";
import "../styles/header.css";

export default function Header() {
  const [menuOpen, setMenuOpen] = useState(false);
  const navRef = useRef();

  // Close when clicking outside
  useEffect(() => {
    const handleClickOutside = (e) => {
      if (navRef.current && !navRef.current.contains(e.target)) {
        setMenuOpen(false);
      }
    };
    document.addEventListener("mousedown", handleClickOutside);
    return () => document.removeEventListener("mousedown", handleClickOutside);
  }, []);

  // Close when resizing back to desktop
  useEffect(() => {
    const handleResize = () => {
      if (window.innerWidth > 880) setMenuOpen(false);
    };
    window.addEventListener("resize", handleResize);
    return () => window.removeEventListener("resize", handleResize);
  }, []);

  // Close when link clicked
  const handleLinkClick = () => setMenuOpen(false);

  return (
    <header className="wn-header">
      <div className="wn-container wn-header-container">
        <a href="/" className="wn-logo-area">
          <img src="/logo192.png" alt="WhatNew Logo" className="wn-logo" />
          <h1 className="wn-title">WhatNew</h1>
        </a>

        <nav
          ref={navRef}
          className={`wn-nav ${menuOpen ? "open" : ""}`}
          aria-expanded={menuOpen}
        >
          <ul>
            <li><a href="/" onClick={handleLinkClick}>Home</a></li>
            <li><a href="/sell" onClick={handleLinkClick}>Become a Seller</a></li>
            <li><a href="/blog" onClick={handleLinkClick}>Blog</a></li>
            <li><a href="/about" onClick={handleLinkClick}>About Us</a></li>
            <li><a href="/careers" onClick={handleLinkClick}>Careers</a></li>
          </ul>
        </nav>

        <button
          className={`wn-burger ${menuOpen ? "active" : ""}`}
          onClick={() => setMenuOpen(!menuOpen)}
          aria-label="Toggle menu"
        >
          <div className="bar"></div>
          <div className="bar"></div>
          <div className="bar"></div>
        </button>
      </div>
    </header>
  );
}
