import React, { useEffect, useState } from "react";
import axios from "axios";
import "./career.css";
export default function CareerJobs() {
  const [jobs, setJobs] = useState([]);

  useEffect(() => {
    axios
      .get("http://localhost:5000/jobs")
      .then((res) => setJobs(res.data))
      .catch((err) => console.log(err));
  }, []);

  return (
    <section className="job-list-section" style={{ padding: "50px 0" }}>
      <div className="job-list-container" style={{ width: "90%", margin: "auto" }}>
        <h2 className="job-title" style={{ marginBottom: "20px" }}>
          Open Positions
        </h2>

        {jobs.length === 0 ? (
          <p>No jobs available right now.</p>
        ) : (
          jobs.map((job) => (
            <div
              key={job._id}
              className="job-card"
              style={{
                border: "1px solid #ddd",
                padding: "20px",
                marginBottom: "15px",
                borderRadius: "10px",
              }}
            >
              <h3>{job.title}</h3>
              <p>
                <strong>Company:</strong> {job.company}
              </p>
              <p>
                <strong>Location:</strong> {job.location}
              </p>
              <p>
                <strong>Salary:</strong> {job.salary}
              </p>
              <p>{job.description}</p>

              <button
                className="apply-btn"
                style={{
                  background: "#000",
                  color: "#fff",
                  padding: "10px 15px",
                  border: "none",
                  borderRadius: "8px",
                  cursor: "pointer",
                  marginTop: "10px",
                }}
              >
                Apply Now
              </button>
            </div>
          ))
        )}
      </div>
    </section>
  );
}
