import React, { useState, useEffect, useRef } from "react";
import "./BecomeSeller.css";

const HomePage = () => {
  const [currentIndex, setCurrentIndex] = useState(0);
  const [activeFAQ, setActiveFAQ] = useState(null);
  const fadeRefs = useRef([]);

  // Testimonials auto-slide
  useEffect(() => {
    const interval = setInterval(() => {
      setCurrentIndex((prev) =>
        prev === testimonials.length - 1 ? 0 : prev + 1
      );
    }, 5000);
    return () => clearInterval(interval);
  }, []);

  // Fade-up on scroll using IntersectionObserver
  useEffect(() => {
    const observer = new IntersectionObserver(
      (entries) => {
        entries.forEach((entry) => {
          if (entry.isIntersecting) {
            entry.target.classList.add("show");
          }
        });
      },
      { threshold: 0.2 }
    );

    fadeRefs.current.forEach((el) => {
      if (el) observer.observe(el);
    });

    return () => observer.disconnect();
  }, []);

  const toggleFAQ = (index) => {
    setActiveFAQ(activeFAQ === index ? null : index);
  };

  return (
    <div>
      {/* ===== Promo Section ===== */}
      <div
        className="promo-container fade-up"
        ref={(el) => (fadeRefs.current[0] = el)}
      >
        <div className="promo-content">
          <h1>Make more selling what you love.</h1>
          <p>Show and sell. Go live on WhatNew.</p>
        </div>
      </div>

      {/* ===== WhatNew Section ===== */}
      <div
        className="whatnew-container fade-up stagger-1"
        ref={(el) => (fadeRefs.current[1] = el)}
      >
        <div className="whatnew-content">
          <h1>What is WhatNew?</h1>
          <p>
            WhatNew is a shopping experience like no other, where anyone can
            sell what they love.
          </p>
        </div>
      </div>

      {/* ===== Cards Section ===== */}
      <div className="cards-container">
        {cardsData.map((card, index) => (
          <div
            className={`card fade-up stagger-${index + 1}`}
            key={index}
            ref={(el) => (fadeRefs.current[index + 2] = el)}
          >
            <h2>{card.title}</h2>
            <p>{card.description}</p>
          </div>
        ))}
      </div>

      {/* ===== Sellers Section ===== */}
      <div
        className="sellers-section fade-up stagger-1"
        ref={(el) => (fadeRefs.current[5] = el)}
      >
        <div className="sellers-image">
          <img src="assets/Become1.jpg" alt="Selling on WhatNew" className="fade-up" />
        </div>
        <div className="sellers-content">
          <h2>Discover why sellers are choosing WhatNew</h2>
          <h3>Make more selling your way</h3>
          <p>Selling live on WhatNew is exciting...</p>
          <h3>Reach a huge audience of enthusiasts</h3>
          <p>Connect with millions of buyers on WhatNew...</p>
          <h3>We make it easy</h3>
          <p>Our integrated tools and support handle payments...</p>
          <h3>Sell with joy</h3>
          <p>Live selling on WhatNew isn’t just exciting—it’s pure fun...</p>
        </div>
      </div>

      {/* ===== Testimonial Carousel ===== */}
      <div
        className="testimonial-carousel-container fade-up stagger-2"
        ref={(el) => (fadeRefs.current[6] = el)}
      >
        <h1>Hear from real sellers</h1>
        <div className="carousel-wrapper">
          <div className="testimonial-card fade-up">
            <p className="testimonial-content">
              "{testimonials[currentIndex].content}"
            </p>
            <div className="testimonial-info">
              <img
                src={testimonials[currentIndex].image}
                alt={testimonials[currentIndex].name}
                className="testimonial-img fade-up"
              />
              <div>
                <h3 className="testimonial-name">{testimonials[currentIndex].name}</h3>
                <p className="testimonial-title">{testimonials[currentIndex].title}</p>
              </div>
            </div>
          </div>
        </div>
      </div>

      {/* ===== How It Works ===== */}
      <div
        className="how-it-works-container fade-up stagger-1"
        ref={(el) => (fadeRefs.current[7] = el)}
      >
        <h2>How It Works</h2>
      </div>

      {/* ===== Feature Section ===== */}
      <div className="feature-section fade-up" ref={(el) => (fadeRefs.current[8] = el)}>
        <div className="feature-left">
          <img src="assets/How.png" alt="Feature" className="fade-up" />
        </div>
        <div className="feature-right fade-up">
          <h2>Sign up</h2>
          <p>Whether you’re a seasoned pro or just starting out...</p>
        </div>
      </div>

      {/* ===== Reverse Section ===== */}
      <div className="reverse-section fade-up" ref={(el) => (fadeRefs.current[9] = el)}>
        <div className="reverse-left fade-up">
          <h2>Powerful Features</h2>
          <p>When it comes to selling on WhatNew...</p>
          <h2>Sell in a live show</h2>
          <ul>
            <li>Host a show and chat with your audience in real-time.</li>
            <li>Auction off items, sell items at fixed prices, or run giveaways.</li>
            <li>Camera-shy? No problem – feature your products front-and-center!</li>
            <li>Show off your products and geek out with excited buyers.</li>
          </ul>
          <h2>Sell from your WhatNew store 24/7</h2>
          <ul>
            <li>List products and let buyers shop anytime.</li>
            <li>Keep making sales even when you’re not hosting a show.</li>
          </ul>
        </div>
        <div className="reverse-right">
          <img src="assets/sell.png" alt="Feature" className="fade-up" />
        </div>
      </div>

      {/* ===== Image Right Section ===== */}
      <div className="image-right-section fade-up" ref={(el) => (fadeRefs.current[10] = el)}>
        <div className="text-left fade-up">
          <h2>Ship and get paid</h2>
          <p>Grab your pre-paid WhatNew shipping labels...</p>
        </div>
        <div className="image-right">
          <img src="assets/ship.jpg" alt="Seamless Experience" className="fade-up" />
        </div>
      </div>

      {/* ===== FAQ Section ===== */}
      <div className="faq-container fade-up" ref={(el) => (fadeRefs.current[11] = el)}>
        <h2>Frequently Asked Questions</h2>
        <div className="faq-list">
          {faqs.map((faq, index) => (
            <div
              className={`faq-item ${activeFAQ === index ? "active" : ""} fade-up`}
              key={index}
              onClick={() => toggleFAQ(index)}
            >
              <div className="faq-question">
                <h3>{faq.question}</h3>
                <span>{activeFAQ === index ? "−" : "+"}</span>
              </div>
              {activeFAQ === index && (
                <div className="faq-answer">
                  <p>{faq.answer}</p>
                </div>
              )}
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default HomePage;
