import React from "react";
import "./Home.css";

const HomePage = () => {
  return (
    <div className="home-wrapper">

      {/* --- First Box --- */}
      <div className="home-box">
        <div className="home-image">
          <img src="assets/Hero.png" alt="Live Shopping" />
        </div>
        <div className="home-content">
          <h1>The Live Shopping Marketplace</h1>
          <p>Shop, sell, and connect around the things you love.</p>
          <button className="download-btn">Shop Now</button>
        </div>
      </div>
    
    
<div className="home-container">
      {/* Join In the Fun Section */}
      <section className="fun-section">
        <div className="fun-content">
          <h2>Join In the Fun</h2>
          <p>
            Take part in fast-paced auctions, incredible flash sales, live show
            giveaways, and so much more.
          </p>
          <button className="download-btn">Shop Now</button>
        </div>
        <div className="home-image">
          <img src="assets/Hero.png" alt="Live Shopping" />
        </div>
      </section>
    </div>

    </div>
  );
};

export default HomePage;
