import React, { useRef } from "react";
import emailjs from "@emailjs/browser";
import "./Home.css";

const Home = () => {
  const form = useRef();

  // Send email function
  const sendEmail = (e) => {
    e.preventDefault();

    emailjs
      .sendForm(
        "service_qompota", // üîπ Replace with your EmailJS Service ID
        "template_95iekz2", // üîπ Replace with your EmailJS Template ID
        form.current,
        "tkG-UJe02yYKmGQ-m"   // üîπ Replace with your EmailJS Public Key
      )
      .then(
        (result) => {
          console.log(result.text);
          alert("‚úÖ Subscription successful! Thank you.");
          form.current.reset();
        },
        (error) => {
          console.log(error.text);
          alert("‚ùå Something went wrong. Please try again.");
        }
      );
  };

  return (
    <div>
    {/* ===== First Box ===== */}
   
<div className="box-container">
  <div className="box-left">
    <h1>The Live Shopping Marketplace</h1>
    <p>Shop, sell, and connect around the things you love.</p>

    {/* ‚úÖ Button links to WhatNot site */}
    


      <button>Shop Now</button>
  
  </div>

  <div className="box-right">
    <img src="assets/Hero.png" alt="Live Shopping" />
  </div>
</div>



      {/* ===== Second Box ===== */}
      <div className="image-left-section">
        <div className="image-column">
          <img src="assets/Hero2.jpeg" alt="Live Shopping" />
        </div>
        <div className="content-column">
          <h1>Join In the Fun</h1>
          <p>
            Take part in fast-paced auctions, incredible flash sales, live show
            giveaways, and so much more.
          </p>
          <button>Shop Now</button>
        </div>
      </div>

      {/* ===== Third Box ===== */}
      <div className="third-box-container">
        <div className="third-box-right">
          <h1>We've Got It All</h1>
          <p>
            Explore 250+ categories, including fashion, coins, sports & Pok√©mon
            cards, sneakers, and more.
          </p>
          <button>Shop Now</button>
        </div>
        <div className="third-box-left">
          <img src="assets/c425.jpeg" alt="Exciting Shopping" />
        </div>
      </div>

      {/* ===== Fourth Box ===== */}
      <div className="fourth-box-container">
        <div className="fourth-box-left">
          <h1>Find Incredible Deals on Name Brands</h1>
          <p>
            From the brands you love, to hard-to-find specialty products. There's
            a deal on whatever you're looking for.
          </p>
          <button>Shop Now</button>
        </div>
        <div className="fourth-box-right">
          <img src="assets/images.png.png" alt="Amazing Deals" />
        </div>
      </div>

      {/* ===== Subscribe Section ===== */}
      <div className="subscribe-container">
        <h2>Subscribe to Our Newsletter</h2>
        <p>Stay updated with the latest deals, offers, and live shopping events!</p>

        <form ref={form} onSubmit={sendEmail} className="subscribe-box">
          <input
            type="email"
            name="user_email"
            placeholder="Enter your email"
            required
          />
          <button type="submit">Subscribe</button>
        </form>
      </div>
    </div>
  );
};
export default Home;
