import React, { useState, useEffect } from "react";

export default function Career() {
  const [jobs, setJobs] = useState([]);
  const [newJob, setNewJob] = useState({
    title: "", description: "", location: "", type: "", salary: ""
  });

  useEffect(() => {
    fetch("http://localhost:5000/jobs")
      .then(res => res.json())
      .then(data => setJobs(data));
  }, []);

  const handlePostJob = async () => {
    await fetch("http://localhost:5000/jobs", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(newJob),
    });
    const refreshed = await fetch("http://localhost:5000/jobs").then(res => res.json());
    setJobs(refreshed);
  };

  return (
    <div>
      <h2>Jobs</h2>
      <input placeholder="Title" onChange={e => setNewJob({ ...newJob, title:e.target.value })}/>
      <button onClick={handlePostJob}>Post Job</button>
      {jobs.map(j => <div key={j._id}>{j.title}</div>)}
    </div>
  );
}
