import React, { useState } from "react";
import "./Blog.css";

const TabsGrid = () => {
  const [activeTab, setActiveTab] = useState(1);

  // Create data for 5 tabs, 3 rows × 5 columns
  const tabsData = Array.from({ length: 5 }, (_, tabIndex) => ({
    tabName: `Tab ${tabIndex + 1}`,
    content: Array.from({ length: 3 }, (_, rowIndex) =>
      Array.from({ length: 5 }, (_, colIndex) => ({
        img: `https://via.placeholder.com/100?text=T${tabIndex+1}-R${rowIndex+1}C${colIndex+1}`,
        title: `Title ${rowIndex + 1}-${colIndex + 1}`,
        desc: `Description ${rowIndex + 1}-${colIndex + 1}`,
      }))
    )
  }));

  return (
    <div className="tabs-container">
      {/* Tabs Header */}
      <div className="tabs-header">
        {tabsData.map((tab, index) => (
          <button
            key={index}
            className={activeTab === index + 1 ? "active" : ""}
            onClick={() => setActiveTab(index + 1)}
          >
            {tab.tabName}
          </button>
        ))}
      </div>

      {/* Tabs Content */}
      <div className="tabs-content">
        {tabsData.map((tab, index) => (
          <div
            key={index}
            className={`tab-panel ${activeTab === index + 1 ? "active" : ""}`}
          >
            {tab.content.map((row, rowIndex) => (
              <div key={rowIndex} className="tab-row">
                {row.map((item, colIndex) => (
                  <div key={colIndex} className="tab-item">
                    <img src={item.img} alt={item.title} />
                    <h4>{item.title}</h4>
                    <p>{item.desc}</p>
                  </div>
                ))}
              </div>
            ))}
          </div>
        ))}
      </div>
    </div>
  );
};

export default TabsGrid;
