import React, { useEffect, useState } from "react";
import axios from "axios";
import "./Discovery.css";

import Header2 from "../components/Header2";

export default function Discovery() {
  const [jobs, setJobs] = useState([]);
  const [selectedJob, setSelectedJob] = useState(null);
  const [resume, setResume] = useState(null);
  const [newJob, setNewJob] = useState({
    title: "",
    description: "",
    location: "",
    type: "",
    salary: "",
  });

  // Pagination
  const [currentPage, setCurrentPage] = useState(1);
  const jobsPerPage = 3;

  const API_URL = "http://localhost:5000"; // ðŸ”¥ BACKEND URL

  // ----------------------------------------------------
  // ðŸ”¥ 1. FETCH JOBS FROM BACKEND
  // ----------------------------------------------------
  const fetchJobs = async () => {
    try {
      const res = await axios.get(`${API_URL}/jobs`);
      setJobs(res.data);
    } catch (err) {
      console.error("Error fetching jobs:", err);
    }
  };

  useEffect(() => {
    fetchJobs();
  }, []);

  // ----------------------------------------------------
  // ðŸ”¥ 2. APPLY FOR JOB
  // ----------------------------------------------------
  const handleApply = async (job) => {
    if (!resume) return alert("Please upload a resume first!");

    const formData = new FormData();
    formData.append("jobId", job._id);
    formData.append("resume", resume);

    try {
      await axios.post(`${API_URL}/apply`, formData, {
        headers: { "Content-Type": "multipart/form-data" },
      });

      alert(`Applied to ${job.title} successfully!`);
      setSelectedJob(null);
      setResume(null);
    } catch (err) {
      console.error("Apply failed:", err);
      alert("Failed to apply");
    }
  };

  // ----------------------------------------------------
  // ðŸ”¥ 3. POST NEW JOB
  // ----------------------------------------------------
  const handlePostJob = async () => {
    if (!newJob.title || !newJob.description)
      return alert("Please fill all fields!");

    try {
      await axios.post(`${API_URL}/jobs`, newJob);
      alert("Job posted successfully!");

      setNewJob({
        title: "",
        description: "",
        location: "",
        type: "",
        salary: "",
      });

      fetchJobs(); // reload list
    } catch (err) {
      console.error("Error posting job:", err);
      alert("Failed to post job");
    }
  };

  // Pagination logic
  const indexOfLastJob = currentPage * jobsPerPage;
  const indexOfFirstJob = indexOfLastJob - jobsPerPage;
  const currentJobs = jobs.slice(indexOfFirstJob, indexOfLastJob);
  const totalPages = Math.ceil(jobs.length / jobsPerPage);

  return (
    <div className="career-container">
      <Header2 />
      <h2 className="career-title">Career Opportunities</h2>

      {/* Job List */}
      <div className="job-list">
        {currentJobs.map((job) => (
          <div className="job-card" key={job._id}>
            <h3>{job.title}</h3>
            <p>{job.description}</p>
            <p><strong>Location:</strong> {job.location}</p>
            <p><strong>Type:</strong> {job.type}</p>
            <p><strong>Salary:</strong> {job.salary}</p>
            <button onClick={() => setSelectedJob(job)}>Apply Now</button>
          </div>
        ))}
      </div>

      {/* Pagination */}
      <div className="pagination">
        {Array.from({ length: totalPages }, (_, i) => (
          <button
            key={i + 1}
            onClick={() => setCurrentPage(i + 1)}
            className={currentPage === i + 1 ? "active-page" : ""}
          >
            {i + 1}
          </button>
        ))}
      </div>

      {/* Apply Form */}
      {selectedJob && (
        <div className="apply-form">
          <h3>Apply for: {selectedJob.title}</h3>
          <input
            type="file"
            accept=".pdf,.doc,.docx"
            onChange={(e) => setResume(e.target.files[0])}
          />
          <button onClick={() => handleApply(selectedJob)}>
            Submit Application
          </button>
          <button
            onClick={() => setSelectedJob(null)}
            className="cancel-btn"
          >
            Cancel
          </button>
        </div>
      )}

      {/* Post Job Form */}
      <div className="post-job-form">
        <h3>Post a New Job</h3>

        <input
          type="text"
          name="title"
          placeholder="Job Title"
          value={newJob.title}
          onChange={(e) =>
            setNewJob({ ...newJob, title: e.target.value })
          }
        />

        <textarea
          name="description"
          placeholder="Job Description"
          value={newJob.description}
          onChange={(e) =>
            setNewJob({ ...newJob, description: e.target.value })
          }
        ></textarea>

        <input
          type="text"
          name="location"
          placeholder="Location"
          value={newJob.location}
          onChange={(e) =>
            setNewJob({ ...newJob, location: e.target.value })
          }
        />

        <input
          type="text"
          name="type"
          placeholder="Job Type"
          value={newJob.type}
          onChange={(e) =>
            setNewJob({ ...newJob, type: e.target.value })
          }
        />

        <input
          type="text"
          name="salary"
          placeholder="Salary"
          value={newJob.salary}
          onChange={(e) =>
            setNewJob({ ...newJob, salary: e.target.value })
          }
        />

        <button onClick={handlePostJob}>Post Job</button>
      </div>
    </div>
  );
}
