// HeroSection.jsx
import React from "react";
import "./Payment.css";
import BackgroundImg from "../assets/Hero2.jpeg"; // <-- use correct relative path!

const HeroSection = () => {
  return (
    <div
      className="hero-section"
      style={{
        backgroundImage: `url(${BackgroundImg})`,
        backgroundSize: "cover",
        backgroundPosition: "center",
      }}
    >
      <div className="overlay">
        <nav className="breadcrumb">
          <a href="/">Home</a> / <span>Live Shopping</span>
        </nav>
        <h1>Welcome to Live Shopping</h1>
        <p>Shop, sell, and connect around the things you love</p>
      </div>
    </div>
  );
};

export default HeroSection;
