import React, { useState, useEffect } from "react";
import axios from "axios";
import "./Discovery.css";
import Header2 from "../components/Header2";

export default function Career() {
  const [jobs, setJobs] = useState([]);
  const [selectedJob, setSelectedJob] = useState(null);
  const [resume, setResume] = useState(null);
  const [newJob, setNewJob] = useState({
    title: "",
    description: "",
    location: "",
    type: "",
    salary: "",
  });

  const [currentPage, setCurrentPage] = useState(1);
  const jobsPerPage = 3;

  // ðŸ”¥ Fetch jobs from backend (MongoDB)
  useEffect(() => {
    axios
      .get("http://localhost:5000/jobs")
      .then((res) => setJobs(res.data))
      .catch((err) => console.log("Error fetching jobs:", err));
  }, []);

  const indexOfLastJob = currentPage * jobsPerPage;
  const indexOfFirstJob = indexOfLastJob - jobsPerPage;
  const currentJobs = jobs.slice(indexOfFirstJob, indexOfLastJob);

  const totalPages = Math.ceil(jobs.length / jobsPerPage);

  const paginate = (page) => setCurrentPage(page);

  // APPLY FOR JOB
  const handleApply = (job) => {
    if (!resume) return alert("Upload a resume first!");

    const formData = new FormData();
    formData.append("jobId", job._id);
    formData.append("resume", resume);

    axios
      .post("http://localhost:5000/apply", formData)
      .then(() => {
        alert("Application submitted!");
        setSelectedJob(null);
      })
      .catch((err) => console.log("Apply Error:", err));
  };

  // UPDATE POST JOB FORM
  const handleNewJobChange = (e) =>
    setNewJob({ ...newJob, [e.target.name]: e.target.value });

  // POST NEW JOB
  const handlePostJob = () => {
    axios
      .post("http://localhost:5000/post-job", newJob)
      .then(() => {
        alert("Job posted!");
        setNewJob({
          title: "",
          description: "",
          location: "",
          type: "",
          salary: "",
        });

        // refresh list
        return axios.get("http://localhost:5000/jobs");
      })
      .then((res) => setJobs(res.data))
      .catch((err) => console.log("Post Job Error:", err));
  };

  return (
    <div className="career-container">
      <Header2 />
      <h2 className="career-title">Career Opportunities</h2>

      {/* JOB LIST */}
      <div className="job-list">
        {currentJobs.map((job) => (
          <div className="job-card" key={job._id}>
            <h3>{job.title}</h3>
            <p>{job.description}</p>
            <p><strong>Location:</strong> {job.location}</p>
            <p><strong>Type:</strong> {job.type}</p>
            <p><strong>Salary:</strong> {job.salary}</p>
            <button onClick={() => setSelectedJob(job)}>Apply Now</button>
          </div>
        ))}
      </div>

      {/* PAGINATION */}
      <div className="pagination">
        {Array.from({ length: totalPages }).map((_, i) => (
          <button
            key={i + 1}
            onClick={() => paginate(i + 1)}
            className={currentPage === i + 1 ? "active-page" : ""}
          >
            {i + 1}
          </button>
        ))}
      </div>

      {/* APPLY FORM */}
      {selectedJob && (
        <div className="apply-form">
          <h3>Apply for: {selectedJob.title}</h3>
          <input type="file" onChange={(e) => setResume(e.target.files[0])} />
          <button onClick={() => handleApply(selectedJob)}>Submit</button>
          <button className="cancel-btn" onClick={() => setSelectedJob(null)}>
            Cancel
          </button>
        </div>
      )}

      {/* POST JOB FORM */}
      <div className="post-job-form">
        <h3>Post a Job</h3>

        <input
          type="text"
          name="title"
          placeholder="Title"
          value={newJob.title}
          onChange={handleNewJobChange}
        />
        <textarea
          name="description"
          placeholder="Description"
          value={newJob.description}
          onChange={handleNewJobChange}
        ></textarea>
        <input
          type="text"
          name="location"
          placeholder="Location"
          value={newJob.location}
          onChange={handleNewJobChange}
        />
        <input
          type="text"
          name="type"
          placeholder="Type"
          value={newJob.type}
          onChange={handleNewJobChange}
        />
        <input
          type="text"
          name="salary"
          placeholder="Salary"
          value={newJob.salary}
          onChange={handleNewJobChange}
        />
        <button onClick={handlePostJob}>Post Job</button>
      </div>
    </div>
  );
}
