import React from "react";
import "./shipping.css";

const SingleLineText = () => {
  return (
    <div className="single-line-container">
      <p>
        This is a very long paragraph that should stay on a single line without
        wrapping. If the content exceeds the width of the container, it will
        scroll horizontally instead of breaking into multiple lines, ensuring
        that the entire text remains in one continuous line for better readability
        in some UI use cases like labels, banners, or marquees.
      </p>
    </div>
  );
};

export default SingleLineText;
