import React, { useState, useEffect } from "react";
import "./About.css";

/* ===== Text Carousel ===== */
const CarouselText = () => {
  const slides = [
    {
      title: "Thehustle.Co",
      text: "It brings together like-minded people who can chat and geek-out in real time.",
    },
    {
      title: "Dot.La",
      text: "It's probably one of the fastest growing marketplaces we've ever seen.",
    },
  ];

  const [currentIndex, setCurrentIndex] = useState(0);

  // Auto-slide every 5 seconds
  useEffect(() => {
    const interval = setInterval(() => {
      setCurrentIndex((prevIndex) =>
        prevIndex === slides.length - 1 ? 0 : prevIndex + 1
      );
    }, 5000);
    return () => clearInterval(interval);
  }, [slides.length]);

  return (
    <div className="carousel-text-container">
      <div className="carousel-slide">
        <h2 className="carousel-title">{slides[currentIndex].title}</h2>
        <p className="carousel-description">{slides[currentIndex].text}</p>
      </div>
    </div>
  );
};

/* ===== New Image Slider Section ===== */
const ImageSlider = () => {
  const images = [
    "/assets/OurHome.png",
    "/assets/OurHome-2.png",
    
   
  ];

  const [currentIndex, setCurrentIndex] = useState(0);

  // Auto-slide every 5 seconds
  useEffect(() => {
    const interval = setInterval(() => {
      setCurrentIndex((prevIndex) =>
        prevIndex === images.length - 1 ? 0 : prevIndex + 1
      );
    }, 5000);
    return () => clearInterval(interval);
  }, [images.length]);

  return (
    <div className="image-slider-section">
      <h2 className="section-heading">Our Home</h2>
      <div className="image-slider">
        <img
          src={images[currentIndex]}
          alt={`Slide ${currentIndex}`}
          className="slider-image"
        />
      </div>
    </div>
  );
};

/* ===== About Page ===== */
const About = () => {
  const investorImages = [
    "/assets/partner.png",
    "/assets/partner-y.png",
    "/assets/partner-x.png",
    "/assets/operator.png",
    "/assets/partner 1.png",
    "/assets/partne r2.png",
    "/assets/partner3.png",
    "/assets/partner4.png",
  ];

  return (
    <div className="about-container">
      {/* ===== About Section ===== */}
      <div className="divbox-container">
        <div className="divbox-text">
          <h2>About Us</h2>
          <p>
            We aim to enable anyone to turn their passion into a business & bring
            people together through commerce.
          </p>
        </div>

        <div className="divbox-image">
          <img src="/assets/Hero5.png" alt="About Us" />
        </div>
      </div>

      {/* ===== Investors Section ===== */}
      <div className="imagegrid-section">
        <h2 className="section-heading">Our Investors</h2>
        <div className="image-grid">
          {investorImages.map((img, index) => (
            <div key={index} className="image-card">
              <img src={img} alt={`Investor ${index + 1}`} />
            </div>
          ))}
        </div>
      </div>

      {/* ===== Text Carousel ===== */}
      <CarouselText />

      {/* ===== New Image Slider Section ===== */}
      <ImageSlider />
    </div>
  );
};

export default About;
