import React, { useEffect, useState } from "react";
import axios from "axios";
import "./career.css";

export default function Career() {
  const [jobs, setJobs] = useState([]);
  const [loading, setLoading] = useState(true);
  const [newJob, setNewJob] = useState({
    title: "",
    company: "",
    location: "",
    type: "",
    salary: "",
    description: "",
  });

  // Fetch jobs from backend
  const fetchJobs = () => {
    setLoading(true);
    axios
      .get("http://localhost:5000/jobs")
      .then((res) => setJobs(res.data))
      .catch((err) => {
        console.error("Error fetching jobs:", err);
        setJobs([]);
      })
      .finally(() => setLoading(false));
  };

  useEffect(() => {
    fetchJobs();
  }, []);

  // Handle form input
  const handleChange = (e) => {
    setNewJob({ ...newJob, [e.target.name]: e.target.value });
  };

  // Submit new job
  const handleSubmit = (e) => {
    e.preventDefault();
    axios
      .post("http://localhost:5000/jobs", newJob)
      .then(() => {
        alert("Job posted successfully!");
        setNewJob({
          title: "",
          company: "",
          location: "",
          type: "",
          salary: "",
          description: "",
        });
        fetchJobs();
      })
      .catch((err) => {
        console.error("Error posting job:", err);
        alert("Failed to post job.");
      });
  };

  // Apply to a job
  const handleApply = (jobId) => {
    axios
      .post(`http://localhost:5000/jobs/${jobId}/apply`, { applicant: "Test User" })
      .then(() => alert("Applied successfully!"))
      .catch((err) => alert("Failed to apply."));
  };

  if (loading) return <div className="loading">Loading jobs...</div>;

  return (
    <div className="career-container">
      <h2>Post a New Job</h2>
      <form className="job-form" onSubmit={handleSubmit}>
        <input
          type="text"
          name="title"
          placeholder="Job Title"
          value={newJob.title}
          onChange={handleChange}
          required
        />
        <input
          type="text"
          name="company"
          placeholder="Company"
          value={newJob.company}
          onChange={handleChange}
          required
        />
        <input
          type="text"
          name="location"
          placeholder="Location"
          value={newJob.location}
          onChange={handleChange}
          required
        />
        <input
          type="text"
          name="type"
          placeholder="Job Type"
          value={newJob.type}
          onChange={handleChange}
          required
        />
        <input
          type="text"
          name="salary"
          placeholder="Salary"
          value={newJob.salary}
          onChange={handleChange}
        />
        <textarea
          name="description"
          placeholder="Job Description"
          value={newJob.description}
          onChange={handleChange}
          required
        ></textarea>
        <button type="submit" className="submit-btn">Post Job</button>
      </form>

      <h2>Open Job Positions</h2>
      {jobs.length === 0 ? (
        <div>No jobs available.</div>
      ) : (
        <div className="job-list">
          {jobs.map((job) => (
            <div key={job._id} className="job-card">
              <h3>{job.title}</h3>
              <p><span className="job-label">Company:</span> {job.company}</p>
              <p><span className="job-label">Location:</span> {job.location}</p>
              <p><span className="job-label">Type:</span> {job.type}</p>
              <p><span className="job-label">Salary:</span> {job.salary}</p>
              <p>{job.description}</p>
              <small>Posted on: {new Date(job.postedAt).toLocaleDateString()}</small>
              <button className="apply-btn" onClick={() => handleApply(job._id)}>Apply Now</button>
            </div>
          ))}
        </div>
      )}
    </div>
  );
}
