import React, { useState, useEffect } from "react";
import axios from "axios";
import "./Discovery.css";
import Header2 from "../components/Header2";

export default function Career() {
  const [jobs, setJobs] = useState([]);
  const [selectedJob, setSelectedJob] = useState(null);
  const [resume, setResume] = useState(null);
  const [newJob, setNewJob] = useState({
    title: "",
    description: "",
    location: "",
    type: "",
    salary: "",
  });

  // Pagination
  const [currentPage, setCurrentPage] = useState(1);
  const jobsPerPage = 3;
  const totalPages = Math.ceil(jobs.length / jobsPerPage);

  // Fetch jobs
  useEffect(() => {
    axios
      .get("http://localhost:5001/jobs")
      .then((res) => {
        // ðŸ”¥ ADDED â€” Show only latest 10 jobs & newest first
        const latest10 = res.data.slice(-10).reverse();
        setJobs(latest10);
      })
      .catch((err) => console.error("Error fetching jobs:", err));
  }, []);

  const handleApply = (job) => {
    if (!resume) return alert("Please upload a resume first!");
    alert(`Applied to ${job.title} successfully!`);
    setSelectedJob(null);
    setResume(null);
  };

  const handleNewJobChange = (e) =>
    setNewJob({ ...newJob, [e.target.name]: e.target.value });

  const handlePostJob = () => {
    if (!newJob.title || !newJob.description)
      return alert("Please fill all fields!");

    axios
      .post("http://localhost:5001/jobs", newJob)
      .then((res) => {
        alert(res.data.message);

        // ðŸ”¥ MODIFIED â€” Add new job to TOP + limit to 10
        setJobs((prevJobs) => {
          const updated = [newJob, ...prevJobs];
          return updated.slice(0, 10); // keep only 10
        });

        setNewJob({
          title: "",
          description: "",
          location: "",
          type: "",
          salary: "",
        });
      })
      .catch((err) => console.error("Error posting job:", err));
  };

  // Pagination logic
  const indexOfLastJob = currentPage * jobsPerPage;
  const indexOfFirstJob = indexOfLastJob - jobsPerPage;
  const currentJobs = jobs.slice(indexOfFirstJob, indexOfLastJob);

  return (
    <div className="career-container">
      <Header2 />
      <h2 className="career-title">Career Opportunities</h2>

      {/* Job List */}
      <div className="job-list">
        {currentJobs.map((job) => (
          <div className="job-card" key={job._id}>
            <h3>{job.title}</h3>
            <p>{job.description}</p>
            <p>
              <strong>Location:</strong> {job.location}
            </p>
            <p>
              <strong>Type:</strong> {job.type}
            </p>
            <p>
              <strong>Salary / Package:</strong> {job.salary}
            </p>
            <button onClick={() => setSelectedJob(job)}>Apply Now</button>
          </div>
        ))}
      </div>

      {/* Pagination */}
      <div className="pagination">
        {Array.from({ length: totalPages }, (_, i) => (
          <button
            key={i + 1}
            onClick={() => setCurrentPage(i + 1)}
            className={currentPage === i + 1 ? "active-page" : ""}
          >
            {i + 1}
          </button>
        ))}
      </div>

      {/* Apply Form */}
      {selectedJob && (
        <div className="apply-form">
          <h3>Apply for: {selectedJob.title}</h3>
          <p>
            <strong>Salary/Package:</strong> {selectedJob.salary}
          </p>

          <input
            type="file"
            accept=".pdf,.doc,.docx"
            onChange={(e) => setResume(e.target.files[0])}
          />
          <button onClick={() => handleApply(selectedJob)}>
            Submit Application
          </button>
          <button
            onClick={() => setSelectedJob(null)}
            className="cancel-btn"
          >
            Cancel
          </button>
        </div>
      )}

      {/* Post Job */}
      <div className="post-job-form">
        <h3>Post a New Job</h3>

        <input
          type="text"
          name="title"
          placeholder="Job Title"
          value={newJob.title}
          onChange={handleNewJobChange}
        />

        <textarea
          name="description"
          placeholder="Job Description"
          value={newJob.description}
          onChange={handleNewJobChange}
        ></textarea>

        <input
          type="text"
          name="location"
          placeholder="Location"
          value={newJob.location}
          onChange={handleNewJobChange}
        />

        <input
          type="text"
          name="type"
          placeholder="Job Type"
          value={newJob.type}
          onChange={handleNewJobChange}
        />

        <input
          type="text"
          name="salary"
          placeholder="Salary / Package"
          value={newJob.salary}
          onChange={handleNewJobChange}
        />

        <button onClick={handlePostJob}>Post Job</button>
      </div>
    </div>
  );
}
