import React, { useEffect, useState } from "react";
import axios from "axios";
import "./career.css";

export default function Career() {
  const [jobs, setJobs] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    axios
      .get("http://localhost:5000/jobs")
      .then((res) => setJobs(res.data))
      .catch((err) => {
        console.error("Error fetching jobs:", err);
        setJobs([]);
      })
      .finally(() => setLoading(false));
  }, []);

  if (loading) return <div className="loading">Loading jobs...</div>;
  if (jobs.length === 0) return <div className="loading">No jobs available.</div>;

  return (
    <div className="career-container">
      <h2>Open Job Positions</h2>
      <div className="job-list">
        {jobs.map((job) => (
          <div key={job._id} className="job-card">
            <h3>{job.title}</h3>
            <p>
              <span className="job-label">Company:</span> {job.company}
            </p>
            <p>
              <span className="job-label">Location:</span> {job.location}
            </p>
            <p>
              <span className="job-label">Type:</span> {job.type}
            </p>
            <p>
              <span className="job-label">Salary:</span> {job.salary}
            </p>
            <p>{job.description}</p>
            <small>Posted on: {new Date(job.postedAt).toLocaleDateString()}</small>
            <button className="apply-btn">Apply Now</button>
          </div>
        ))}
      </div>
    </div>
  );
}
