import React, { useState } from "react";
import "./Blog.css";

const TabsGrid = () => {
  const [activeTab, setActiveTab] = useState(0);

  const tabsData = [
    {
      tabName: "Tab 1",
      items: Array(15).fill({
        img: "/images/img1.jpg",
        title: "Tab 1 Image",
        desc: "This is a short description for Tab 1 image.",
      }),
    },
    {
      tabName: "Tab 2",
      items: Array(15).fill({
        img: "/images/img2.jpg",
        title: "Tab 2 Image",
        desc: "This is a short description for Tab 2 image.",
      }),
    },
    {
      tabName: "Tab 3",
      items: Array(15).fill({
        img: "/images/img3.jpg",
        title: "Tab 3 Image",
        desc: "This is a short description for Tab 3 image.",
      }),
    },
    {
      tabName: "Tab 4",
      items: Array(15).fill({
        img: "/images/img4.jpg",
        title: "Tab 4 Image",
        desc: "This is a short description for Tab 4 image.",
      }),
    },
    {
      tabName: "Tab 5",
      items: Array(15).fill({
        img: "/images/img5.jpg",
        title: "Tab 5 Image",
        desc: "This is a short description for Tab 5 image.",
      }),
    },
  ];

  return (
    <div className="tabs-container">
      {/* Tabs Header */}
      <div className="tabs-header">
        {tabsData.map((tab, index) => (
          <button
            key={index}
            className={`tab-btn ${activeTab === index ? "active" : ""}`}
            onClick={() => setActiveTab(index)}
          >
            {tab.tabName}
          </button>
        ))}
      </div>

      {/* Tab Content */}
      <div className="tab-content">
        <div className="image-grid">
          {tabsData[activeTab].items.map((item, i) => (
            <div className="image-box" key={i}>
              <img src={item.img} alt={item.title} />
              <h3>{item.title}</h3>
              <p>{item.desc}</p>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default TabsGrid;
